from Objects import pi, D2R, R2D


# it get the index in return the reference RA and DEC 
# this is used for tilling purpose (Aardvark simulation)
def index2coordinateReference(index=0):
    leng = 0.611555  # size of each map: leng x leng (sqr degree)
    DEC_o = float((index - index % 360) / 360) * leng - 45.
    x = D2R * DEC_o
    RA_o = float(index % 360) * leng * (1. + 0.65 * x ** 2 + 0.0 * x ** 4) + 1.
    # print "RA_o va DEC_o : ", RA_o, DEC_o
    return RA_o, DEC_o

