def index2coordinateReferenceXXL(index=0):
    leng = 3.4 # size of each map: leng x leng (sqr degree)
    ind = xxx[index]
    DEC_o = float((ind-ind%50)/50) * leng - 45.
    x = D2R*DEC_o
    RA_o = float(ind%50) * leng * (1.+0.1*x**2+0.0*x**4) + 1.
    return RA_o, DEC_o



